mapper.select:
     select * 
     from s_shop_coupon
     where shop_id = #{shopId}
        and coupon_status != 3

	        and start_date  >  #{qryDate, jdbcType=TIMESTAMP}


           	and end_date  >=  #{qryDate, jdbcType=TIMESTAMP}
	        and start_date  <=  #{qryDate, jdbcType=TIMESTAMP}


           	and end_date  <  #{qryDate, jdbcType=TIMESTAMP}
