mapper.select:
select a.*,b.user_name,b.nick_name
from s_user_relation a,
     s_user b
where relation_type = 1
      and a.parent_id = #{userId}
      and a.user_id = b.id

select count(*)
from s_user_relation
where
 parent_id = #{userId}
   and relation_type = #{relationType,jdbcType=INTEGER}

     and create_time  >  #{beginDate, jdbcType=DATE}


      and create_time  <  #{endDate, jdbcType=DATE}

select id,user_id , parent_id ,relation_type
 from s_user_relation
 where
    user_id = #{userId}
    and relation_type = #{relationType,jdbcType=INTEGER}

select id,user_id , parent_id ,relation_type
 from s_user_relation
 where
    union_id = #{unionId}
    and relation_type = #{relationType,jdbcType=INTEGER}

  select id,user_id , parent_id ,relation_type
from s_user_relation
where
   union_id = #{unionId}
   and parent_id = #{parentId,jdbcType=VARCHAR}
