mapper.select:
select

from s_user_task


   and user_id = #{userId,jdbcType=VARCHAR}


   and pay_status = #{payStatus,jdbcType=INTEGER}


   and channel = #{channel,jdbcType=INTEGER}

SELECT a.*, b.nick_name nickName, c.product_name FROM s_user_task a LEFT JOIN s_user b ON a.user_id = b.id LEFT JOIN s_product c ON a.product_id = c.id where 1=1
and a.order_sn = #{orderSn}
and a.pay_status = #{payStatus}
and b.nick_name like concat('%', #{nickName}, '%')
and c.product_name like concat('%', #{productName}, '%')
ORDER BY create_time desc
